window.onload=function(){var o=document.querySelectorAll(".modal-open");o.forEach(function(o){o.onclick=function(){var n=o.getAttribute("data-modal");document.getElementById(n).style.display="block"}});var n=document.querySelectorAll(".close-btn");n.forEach(function(o){o.onclick=function(){o.closest(".modal").style.display="none"}})};